import * as React from 'react';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useFormik } from 'formik';
// import { Formik } from 'formik';
import * as yup from "yup";
import axios from 'axios';


const theme = createTheme();
const formValidationSchema = yup.object({
  name: yup
    .string("enter product")
    .required("enter your product"),
  description: yup
    .string("enter description")
    .required("enter your description"),
  category: yup
    .string("enter category")
    .required("enter a category")
})



const title={
    color:'blue'
}

export default function DonateForm() {
  const formik = useFormik({
    initialValues: {
      name: "",
      description: "",
      category: ""
    },
    validationSchema: formValidationSchema,
      onSubmit:(values) => {
        axios.post('http://localhost:8080/api/vi/inventory', values)
        .then(response => {
          console.log(response);
  })
}
})




  return (
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
         <Typography component="h1" variant="h3" style={title}>
            <b>The Hope Center</b>
         </Typography>
         <Typography><b>Incoming Donations</b></Typography>

          <Box component="form" noValidate onSubmit={formik.handleSubmit} sx={{ mt: 3 }}>
            <Grid container spacing={2}>
              {/* <Grid item xs={12} sm={6}>
                <TextField
                  autoComplete="given-name"
                  name="firstName"
                  required
                  fullWidth
                  id="firstName"
                  label="First Name"
                  autoFocus
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  id="lastName"
                  label="Last Name"
                  name="lastName"
                  autoComplete="family-name"
                />
              </Grid> */}
              {/* <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                //   id="email"
                  label="Email Address"
                  name="email"
                  autoComplete="email"
                />
              </Grid>

              <Grid item xs={10} sm={6}>
                <TextFields />
              </Grid> */}

              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="nameOfItem"
                  label="Name of Item"
                  name="nameOfItem"
                  autoComplete="name-of-item"
                  autoFocus
                  value={formik.values.name}
                onChange={formik.handleChange}
                />
              </Grid>
              {/* <Grid item xs={12}>
              <TextField
                  required
                  fullWidth
                  id="date"
                  label="Date"
                  name="date"
                  autoComplete="date"
                />
              </Grid> */}
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="category"
                  label="Category"
                  autoComplete="category"
                  value={formik.values.category}
                onChange={formik.handleChange}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  multiline
                  rows={7}
                  name="description"
                  label="Description"
                  autoComplete="description"
                  value={formik.values.description}
                onChange={formik.handleChange}
                />
              </Grid>
            </Grid>
            <Button
              type="submit"
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              submit
            </Button>

          </Box>
        </Box>

      </Container>
    </ThemeProvider>
  );
}
